import React from 'react'


const HeadingWidget = ({widget, updateHeading}) =>{

return (
<div>
    <h1>Heading Widget</h1>
    <input placeholder="Heading text" //onChange={(event) => updateWidget({...widget, type: event.target.value})}
    />
    <select //onChange={(event) => updateWidget({...widget, type: event.target.value})}
    >
        <option>Heading 1</option>
        <option>Heading 2</option>
        <option>Heading 3</option>
        <option>Heading 4</option>
        <option>Heading 5</option>
    </select>
    <h3>Preview</h3>
    {widget.size === "1" && <h2>{widget.text}</h2>}
    {widget.size === "2" && <h2>{widget.text}</h2>}
    {widget.size === "3" && <h2>{widget.text}</h2>}
    {widget.size === "4" && <h2>{widget.text}</h2>}
    {widget.size === "5" && <h2>{widget.text}</h2>}
</div>
)}


export default HeadingWidget
